    function toggleEditableFields() {
        const editableElements = document.querySelectorAll('.editable');
        editableElements.forEach(function (element) {
            const text = element.textContent;
            const inputField = document.createElement('input');
            inputField.setAttribute('type', 'text');
            inputField.value = text;
            element.innerHTML = '';
            element.appendChild(inputField);
        });
    }

    // Save changes and update the HTML
    const saveChangesButton = document.getElementById('saveChanges');
    if (saveChangesButton) {
        saveChangesButton.addEventListener('click', function () {
            const editableInputs = document.querySelectorAll('.editable input');
            editableInputs.forEach(function (input) {
                const text = input.value;
                const parentElement = input.parentElement;
                parentElement.innerHTML = text;
            });
            // Trigger the download button
            const downloadButton = document.getElementById('downloadButton');
            if (downloadButton) {
                downloadButton.style.display = 'block';
            }
        });
    }

    // Function to create a Blob of the updated HTML
    function createBlobFromHTML() {
        const updatedHtml = document.documentElement.outerHTML;
        return new Blob([updatedHtml], { type: 'text/html' });
    }

    // Download button click event
    const downloadButton = document.getElementById('downloadButton');
    if (downloadButton) {
        downloadButton.addEventListener('click', function () {
            const blob = createBlobFromHTML();
            const url = URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = 'updated_project.html';
            a.style.display = 'none';
            document.body.appendChild(a);
            a.click();
            document.body.removeChild(a);
            URL.revokeObjectURL(url);
        });
    }

